+interface EditableFieldProps {
+  value: string;
+  onSave: (value: string) => void;
+  label?: string;
+  placeholder?: string;
+  type?: string;
+}
+
+function EditableField({ value, onSave, label, placeholder, type = 'text' }: EditableFieldProps) {
+  const [isEditing, setIsEditing] = useState(false);
+  const [editValue, setEditValue] = useState(value || '');
+  const inputRef = useRef<HTMLInputElement>(null);
+
+  useEffect(() => {
+    if (isEditing && inputRef.current) {
+      inputRef.current.focus();
+    }
+  }, [isEditing]);
+
+  const handleSave = () => {
+    onSave(editValue);
+    setIsEditing(false);
+  };
+
+  const handleKeyDown = (e: React.KeyboardEvent) => {
+    if (e.key === 'Enter') {
+      handleSave();
+    } else if (e.key === 'Escape') {
+      setEditValue(value);
+      setIsEditing(false);
+    }
+  };
+
+  return (
+    <div className="group relative">
+      {isEditing ? (
+        <div className="bg-muted/50 p-2 rounded-md">
+          {label && <Label className="mb-1 text-xs">{label}</Label>}
+          <Input
+            ref={inputRef}
+            type={type}
+            value={editValue}
+            onChange={(e) => setEditValue(e.target.value)}
+            onBlur={handleSave}
+            onKeyDown={handleKeyDown}
+            placeholder={placeholder}
+            className="border-muted-foreground/20"
+          />
+        </div>
+      ) : (
+        <div className="flex items-center justify-between py-1">
+          <div className="flex-1">
+            {label && <span className="text-xs text-muted-foreground block">{label}</span>}
+            <span className={value ? "" : "text-muted-foreground italic text-sm"}>
+              {value || placeholder || "Not specified"}
+            </span>
+          </div>
+          <Button
+            variant="ghost"
+            size="icon"
+            className="h-6 w-6 opacity-0 group-hover:opacity-100 transition-opacity"
+            onClick={() => setIsEditing(true)}
+          >
+            <Pencil className="h-3 w-3" />
+          </Button>
+        </div>
+      )}
+    </div>
+  );
+}
